                <path id="path0" d="M136.651 66.957 C 113.315 83.444,111.037 92.617,112.096 165.829 C 113.005 228.670,113.323 231.304,120.000 231.304 C 126.377 231.304,127.101 228.406,128.696 196.522 C 130.290 164.638,131.014 161.739,137.391 161.739 C 143.964 161.739,144.401 164.381,145.316 209.565 L 146.283 257.391 161.837 257.391 L 177.391 257.391 177.391 229.729 C 177.391 184.030,184.064 177.391,229.996 177.391 C 270.565 177.391,278.261 185.391,278.261 227.559 L 278.261 257.391 294.049 257.391 L 309.837 257.391 309.017 212.278 C 308.566 187.466,309.288 166.074,310.620 164.742 C 311.953 163.409,313.043 171.096,313.043 181.824 C 313.043 192.551,314.746 202.381,316.828 203.667 C 319.513 205.327,320.271 189.457,319.436 148.996 C 318.151 86.657,316.291 80.817,292.788 65.316 C 276.922 54.852,151.925 56.165,136.651 66.957 M228.611 77.476 C 245.009 93.874,238.775 124.044,217.301 132.208 C 208.365 135.606,207.358 137.202,203.761 153.681 C 201.420 164.403,191.171 168.992,181.330 163.724 C 172.955 159.242,167.284 102.264,173.587 85.930 C 181.144 66.346,212.642 61.506,228.611 77.476 M148.375 113.220 C 150.975 119.996,136.740 135.652,127.980 135.652 C 117.823 135.652,116.498 125.803,125.384 116.345 C 134.461 106.682,145.301 105.209,148.375 113.220 " stroke="none" :fill="color" fill-rule="evenodd"></path>
            </svg>
    </g>
    <g v-else class="chesspiece" :class="pieceTransformStyles">
        <svg
            :x="x"
            :y="y"
            :width="width"
            :height="height"
            :viewBox="viewPort"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
        >
            <image :href="'../../../../images/board/animals/' + name + '_' + avatarColor + '.png'"></image>
        </svg>
    </g>
</template>
<script>
export default {
    props: {
        color: {
            type: String,
        },
        name: {
            type: String,
            required: true,
        },
        x: {
            type: Number,
            required: true,
        },
        y: {
            type: Number,
            required: true,
        },
        transformX: {
            type: Number,
            required: false,
        },
        transformY: {
            type: Number,
            required: false,
        },
        isMoves: {
            type: Boolean,
            required: false,
        },
        width: {
            type: Number,
            required: true,
        },
        height: {
            type: Number,
            required: true,
        },
        avatarColor: Number
    },
    computed: {
        viewPort(){
            if(this.name ==='mouse'){
                return '0 0 300 400'
            }
            return '0 0 400 400'
        },
        pieceTransformStyles() {
            if (this.isMoves) {
                let className = "";
                if (this.transformX) {
                    switch (this.transformX) {
                        case 80:
                            className = "animation-to-right";
                            break;
                        case 240:
                            className = "animation-to-right-2x";
                            break;
                        case -80:
                            className = "animation-to-left";
                            break;
                        case -240:
                            className = "animation-to-left-2x";
                            break;
                    }
                    return className;
                } else if (this.transformY) {
                    switch (this.transformY) {
                        case 80:
                            className = "animation-to-bottom";
                            break;
                        case 320:
                            className = "animation-to-bottom-2x";
                            break;
                        case -80:
                            className = "animation-to-top";
                            break;
                        case -320:
                            className = "animation-to-top-2x";
                            break;
                    }
                    return className;
                }
                return "";
            }
            return {};
        },
    },
};
</script>
<style>
svg image {
    width: 100%;
}
.animation-to-left {
    animation: moveToLeft 0.3s ease-in;
}
.animation-to-left-2x {
    animation: moveToLeft2X 0.5s ease-in;
}
.animation-to-right {
    animation: moveToRight 0.3s ease-in;
}
.animation-to-right-2x {
    animation: moveToRight2X 0.5s ease-in;
}
.animation-to-top {
    animation: moveToTop 0.3s ease-in;
}
.animation-to-top-2x {
    animation: moveToTop2X 0.5s ease-in;
}
.animation-to-bottom {
    animation: moveToBottom 0.3s ease-in;
}
.animation-to-bottom-2x {
    animation: moveToBottom2X 0.5s ease-in;
}

@keyframes moveToLeft {
    from {
        transform: translate(80px, 0);
    }
    to {
        transform: translate(0, 0);
    }
}
@keyframes moveToLeft2X {
    from {
        transform: translate(240px, 0);
    }
    to {
        transform: translate(0, 0);
    }
}

@keyframes moveToRight {
    from {
        transform: translate(-80px, 0);
    }
    to {
        transform: translate(0, 0);
    }
}
@keyframes moveToRight2X {
    from {
        transform: translate(-240px, 0);
    }
    to {
        transform: translate(0, 0);
    }
}

@keyframes moveToTop {
    from {
        transform: translate(0, 80px);
    }
    to {
        transform: translate(0, 0);
    }
}
@keyframes moveToTop2X {
    from {
        transform: translate(0, 320px);
    }
    to {
        transform: translate(0, 0);
    }
}

@keyframes moveToBottom {
    from {
        transform: translate(0, -80px);
    }
    to {
        transform: translate(0, 0);
    }
}
@keyframes moveToBottom2X {
    from {
        transform: translate(0, -320px);
    }
    to {
        transform: translate(0, 0);
    }
}
</style>
